// src/PositionToken.sol

contract PositionToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    constructor(string memory name_, string memory symbol_, address admin) ERC20(name_, symbol_) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MINTER_ROLE, admin);
        _grantRole(BURNER_ROLE, admin);
    }

    function decimals() public pure override returns (uint8) { return 18; }

    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external onlyRole(BURNER_ROLE) {
        _burn(from, amount);
    }
}

// src/SpeculateCore.sol

/**
 * @title SpeculateCore
 * @notice Linear AMM for binary prediction markets
 */
contract SpeculateCore is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant MARKET_CREATOR_ROLE = keccak256("MARKET_CREATOR_ROLE");

    uint256 private constant USDC_TO_E18 = 1e12; // 6 decimals -> 18 decimals
    uint256 public constant MAX_USDC_PER_TRADE = 100_000e6; // 100k USDC soft limit per trade

    enum MarketStatus {
        Active,
        Paused,
        Resolved
    }

    enum OracleType {
        None,           // Manual resolution only
        ChainlinkFeed,  // Chainlink Price Feed
        ChainlinkFunctions // Chainlink Functions for custom data
    }

    enum Comparison {
        Above,
        Below,
        Equals
    }

    struct ResolutionConfig {
        uint256 expiryTimestamp;    // When market expires
        OracleType oracleType;      // Type of oracle to use
        address oracleAddress;      // Chainlink Feed or Functions contract address
        bytes32 priceFeedId;        // Price feed ID (for Chainlink Data Feeds)
        uint256 targetValue;        // Target value to compare against
        Comparison comparison;      // How to compare (Above/Below/Equals)
        bool yesWins;               // Winner (set on resolution)
        bool isResolved;            // Whether market has been resolved
    }

    struct Market {
        PositionToken yes;
        PositionToken no;
        uint256 reserveYes;        // 18 decimals
        uint256 reserveNo;         // 18 decimals
        uint256 usdcVault;         // 6 decimals
        uint256 totalPairsUSDC;    // 6 decimals
        uint16 feeTreasuryBps;     // % fee to treasury (e.g. 100 = 1%)
        uint16 feeVaultBps;        // % fee to vault buffer (e.g. 50 = 0.5%)
        uint16 feeLpBps;           // % fee to liquidity providers (e.g. 50 = 0.5%)
        uint16 maxTradeBps;        // e.g., 500 = 5% of pool per trade
        MarketStatus status;
        bool exists;
        bool sellFees;             // true = charge sell fees, false = no sell fees
        string question;
        address lp;                 // market creator / LP address
        ResolutionConfig resolution; // Resolution configuration
    }

    IERC20 public immutable usdc;
    address public treasury;
    mapping(uint256 => Market) public markets;
    uint256 public marketCount;

    // Chainlink resolver contract (optional - for automated resolution)
    address public chainlinkResolver;

    event MarketCreated(
        uint256 indexed id,
        address yes,
        address no,
        string question,
        uint256 reserveYes,
        uint256 reserveNo,
        uint256 usdcVault,
        uint256 expiryTimestamp,
        OracleType oracleType
    );
    event MarketResolved(uint256 indexed id, bool yesWins, uint256 resolvedPrice);
    event BuyYes(uint256 indexed id, address indexed user, uint256 usdcIn, uint256 tokensOut, uint256 priceE6);
    event BuyNo(uint256 indexed id, address indexed user, uint256 usdcIn, uint256 tokensOut, uint256 priceE6);
    event SellYes(uint256 indexed id, address indexed user, uint256 tokensIn, uint256 usdcOut, uint256 priceE6);
    event SellNo(uint256 indexed id, address indexed user, uint256 tokensIn, uint256 usdcOut, uint256 priceE6);
    event FeeCollected(uint256 indexed id, address indexed trader, uint256 feeTreasury, uint256 feeVault, uint256 feeLp, bool isBuy);

    constructor(address _usdc, address _treasury) {
        require(_usdc != address(0), "zero usdc");
        require(_treasury != address(0), "zero treasury");
        usdc = IERC20(_usdc);
        treasury = _treasury;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MARKET_CREATOR_ROLE, msg.sender);
    }

    function setTreasury(address _treasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_treasury != address(0), "zero treasury");
        treasury = _treasury;
    }

    function setChainlinkResolver(address _resolver) external onlyRole(DEFAULT_ADMIN_ROLE) {
        chainlinkResolver = _resolver;
    }

    function setFees(uint256 id, uint16 newTreasuryBps, uint16 newVaultBps) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].exists, "market not found");
        require(newTreasuryBps + newVaultBps <= 1000, "total fee too high");
        markets[id].feeTreasuryBps = newTreasuryBps;
        markets[id].feeVaultBps = newVaultBps;
    }

    function setSellFees(uint256 id, bool enabled) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].exists, "market not found");
        markets[id].sellFees = enabled;
    }

    function setAllFees(uint256 id, uint16 treasuryBps, uint16 vaultBps, uint16 lpBps) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].exists, "market not found");
        require(treasuryBps + vaultBps + lpBps <= 1000, "total fee too high");
        markets[id].feeTreasuryBps = treasuryBps;
        markets[id].feeVaultBps = vaultBps;
        markets[id].feeLpBps = lpBps;
    }

    function grantMarketCreator(address account) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(MARKET_CREATOR_ROLE, account);
    }

    function createMarket(
        string memory question,
        string memory yesName,
        string memory yesSymbol,
        string memory noName,
        string memory noSymbol,
        uint256 initReserveE18,    // e.g. 1000e18
        uint16 feeBps,
        uint16 maxTradeBps,
        uint256 initUsdc,          // e.g. 1000e6
        uint256 expiryTimestamp,   // When market expires
        OracleType oracleType,     // Type of oracle (None, ChainlinkFeed, ChainlinkFunctions)
        address oracleAddress,     // Chainlink Feed or Functions contract
        bytes32 priceFeedId,       // Price feed ID (for Chainlink Data Feeds)
        uint256 targetValue,       // Target value to compare
        Comparison comparison      // How to compare (Above/Below/Equals)
    ) external onlyRole(MARKET_CREATOR_ROLE) returns (uint256 id) {
        require(feeBps <= 1000, "fee too high");
        require(maxTradeBps > 0 && maxTradeBps <= 10000, "invalid maxTradeBps");
        require(initUsdc >= 1e6, "min 1 USDC");
        require(initReserveE18 >= 1e18, "min reserve");

        id = ++marketCount;

        // Deploy tokens
        PositionToken yes = new PositionToken(yesName, yesSymbol, address(this));
        PositionToken no = new PositionToken(noName, noSymbol, address(this));

        // Grant roles to this contract
        yes.grantRole(yes.MINTER_ROLE(), address(this));
        yes.grantRole(yes.BURNER_ROLE(), address(this));
        no.grantRole(no.MINTER_ROLE(), address(this));
        no.grantRole(no.BURNER_ROLE(), address(this));

        Market storage m = markets[id];
        m.yes = yes;
        m.no = no;
        m.reserveYes = initReserveE18;
        m.reserveNo = initReserveE18;
        m.feeTreasuryBps = 100;    // 1% to treasury
        m.feeVaultBps = 50;         // 0.5% to vault buffer
        m.feeLpBps = 50;             // 0.5% to liquidity providers
        m.maxTradeBps = maxTradeBps;
        m.status = MarketStatus.Active;
        m.exists = true;
        m.sellFees = false;         // disable sell fees by default
        m.question = question;
        m.lp = msg.sender;          // creator is LP

        // Set resolution configuration
        m.resolution.expiryTimestamp = expiryTimestamp;
        m.resolution.oracleType = oracleType;
        m.resolution.oracleAddress = oracleAddress;
        m.resolution.priceFeedId = priceFeedId;
        m.resolution.targetValue = targetValue;
        m.resolution.comparison = comparison;
        m.resolution.yesWins = false;
        m.resolution.isResolved = false;

        // Pull real liquidity
        usdc.safeTransferFrom(msg.sender, address(this), initUsdc);
        m.usdcVault = initUsdc;
        m.totalPairsUSDC = initUsdc;

        // Mint inventory to contract so it matches reserves
        yes.mint(address(this), initReserveE18);
        no.mint(address(this), initReserveE18);

        emit MarketCreated(id, address(yes), address(no), question, initReserveE18, initReserveE18, initUsdc, expiryTimestamp, oracleType);
    }

    function spotPriceYesE6(uint256 id) public view returns (uint256) {
        Market storage m = markets[id];
        require(m.exists, "market not found");
        uint256 tot = m.reserveYes + m.reserveNo;
        if (tot == 0) return 5e5; // 0.5
        return (m.reserveNo * 1e6) / tot;
    }

    function spotPriceNoE6(uint256 id) public view returns (uint256) {
        Market storage m = markets[id];
        require(m.exists, "market not found");
        uint256 tot = m.reserveYes + m.reserveNo;
        if (tot == 0) return 5e5; // 0.5
        return (m.reserveYes * 1e6) / tot;
    }

    function _buy(uint256 id, bool isYes, uint256 usdcIn)
        internal
        returns (uint256 tokensOut, uint256 priceE6)
    {
        Market storage m = markets[id];
        require(m.status == MarketStatus.Active, "not active");
        require(usdcIn > 0, "zero");
        require(usdcIn <= MAX_USDC_PER_TRADE, "trade exceeds soft limit");

        usdc.safeTransferFrom(msg.sender, address(this), usdcIn);

        // Split fee: treasury, vault buffer, and LP
        uint256 feeTreasury = (usdcIn * m.feeTreasuryBps) / 10000;
        uint256 feeVault = (usdcIn * m.feeVaultBps) / 10000;
        uint256 feeLp = (usdcIn * m.feeLpBps) / 10000;
        uint256 totalFee = feeTreasury + feeVault + feeLp;
        uint256 netUsdc = usdcIn - totalFee;

        // Send treasury fee
        if (feeTreasury > 0) {
            usdc.safeTransfer(treasury, feeTreasury);
        }

        // Send LP fee
        if (feeLp > 0 && m.lp != address(0)) {
            usdc.safeTransfer(m.lp, feeLp);
        }

        // Vault buffer stays inside the vault ΓÇö effectively increases liquidity
        // Note: feeVault is already in the contract, so we just add it to vault accounting

        // 1) price
        priceE6 = isYes ? spotPriceYesE6(id) : spotPriceNoE6(id);
        require(priceE6 > 0, "bad price");

        // 2) tokensOut precise
        uint256 tokens = Math.mulDiv(netUsdc, 1e18, priceE6);

        // 3) update reserves
        uint256 pairsE18 = netUsdc * USDC_TO_E18;

        if (isYes) {
            m.reserveNo += pairsE18;
            require(m.reserveYes >= tokens, "YES inv underflow");
            m.reserveYes -= tokens;
            m.yes.mint(msg.sender, tokens);
        } else {
            m.reserveYes += pairsE18;
            require(m.reserveNo >= tokens, "NO inv underflow");
            m.reserveNo -= tokens;
            m.no.mint(msg.sender, tokens);
        }

        // 4) sync vault (netUsdc + feeVault = total amount staying in vault)
        m.usdcVault += netUsdc;
        m.usdcVault += feeVault;  // Vault buffer increases liquidity
        m.totalPairsUSDC += netUsdc;
        m.totalPairsUSDC += feeVault;

        // 5) Recompute price after updates (post-trade price)
        priceE6 = isYes ? spotPriceYesE6(id) : spotPriceNoE6(id);

        // 6) Sanity check: vault consistency
        assert(m.usdcVault <= usdc.balanceOf(address(this)));

        // 7) Emit fee event
        emit FeeCollected(id, msg.sender, feeTreasury, feeVault, feeLp, true);

        // 8) return
        tokensOut = tokens;
    }

    function buyYes(uint256 id, uint256 usdcIn, uint256 minOut) external nonReentrant {
        (uint256 out, uint256 priceE6) = _buy(id, true, usdcIn);
        require(out >= minOut, "slip");
        emit BuyYes(id, msg.sender, usdcIn, out, priceE6);
    }

    function buyNo(uint256 id, uint256 usdcIn, uint256 minOut) external nonReentrant {
        (uint256 out, uint256 priceE6) = _buy(id, false, usdcIn);
        require(out >= minOut, "slip");
        emit BuyNo(id, msg.sender, usdcIn, out, priceE6);
    }

    function _sell(uint256 id, bool isYes, uint256 tokensIn)
        internal
        returns (uint256 usdcOut, uint256 priceE6)
    {
        Market storage m = markets[id];
        require(m.status == MarketStatus.Active, "not active");
        require(tokensIn > 0, "zero");

        // Pull tokens
        if (isYes) {
            IERC20(address(m.yes)).safeTransferFrom(msg.sender, address(this), tokensIn);
        } else {
            IERC20(address(m.no)).safeTransferFrom(msg.sender, address(this), tokensIn);
        }

        // Price
        priceE6 = isYes ? spotPriceYesE6(id) : spotPriceNoE6(id);

        // Gross usdc
        uint256 grossUsdc = Math.mulDiv(tokensIn, priceE6, 1e18);

        // Soft trade limit (USDC-based)
        require(grossUsdc <= MAX_USDC_PER_TRADE, "trade exceeds soft limit");

        // Solvency check
        require(grossUsdc <= m.usdcVault, "insufficient vault");

        // Reserves updates
        uint256 pairsE18 = grossUsdc * USDC_TO_E18;
        if (isYes) {
            m.reserveYes += tokensIn;
            require(m.reserveNo >= pairsE18, "NO inv underflow");
            m.reserveNo -= pairsE18;
        } else {
            m.reserveNo += tokensIn;
            require(m.reserveYes >= pairsE18, "YES inv underflow");
            m.reserveYes -= pairsE18;
        }

        // --- if sell fees are enabled for this market ---
        if (m.sellFees) {
            uint256 feeTreasury = (grossUsdc * m.feeTreasuryBps) / 10000;
            uint256 feeVault = (grossUsdc * m.feeVaultBps) / 10000;
            uint256 feeLp = (grossUsdc * m.feeLpBps) / 10000;
            uint256 payout = grossUsdc - (feeTreasury + feeVault + feeLp);

            // Vault accounting: money leaving = payout + feeTreasury + feeLp, money staying = feeVault
            m.usdcVault = m.usdcVault - payout - feeTreasury - feeLp + feeVault;
            m.totalPairsUSDC = m.totalPairsUSDC - payout - feeTreasury - feeLp + feeVault;

            usdc.safeTransfer(msg.sender, payout);
            if (feeTreasury > 0) {
                usdc.safeTransfer(treasury, feeTreasury);
            }
            if (feeLp > 0 && m.lp != address(0)) {
                usdc.safeTransfer(m.lp, feeLp);
            }

            // Emit fee event
            emit FeeCollected(id, msg.sender, feeTreasury, feeVault, feeLp, false);
            usdcOut = payout;
        } else {
            // --- fee-free sell ---
            m.usdcVault -= grossUsdc;
            m.totalPairsUSDC -= grossUsdc;
            usdc.safeTransfer(msg.sender, grossUsdc);
            usdcOut = grossUsdc;
        }

        // Recompute price after updates (post-trade price)
        priceE6 = isYes ? spotPriceYesE6(id) : spotPriceNoE6(id);
    }

    function sellYes(uint256 id, uint256 tokensIn, uint256 minUsdcOut) external nonReentrant {
        (uint256 out, uint256 priceE6) = _sell(id, true, tokensIn);
        require(out >= minUsdcOut, "slip");
        emit SellYes(id, msg.sender, tokensIn, out, priceE6);
    }

    function sellNo(uint256 id, uint256 tokensIn, uint256 minUsdcOut) external nonReentrant {
        (uint256 out, uint256 priceE6) = _sell(id, false, tokensIn);
        require(out >= minUsdcOut, "slip");
        emit SellNo(id, msg.sender, tokensIn, out, priceE6);
    }

    function pauseMarket(uint256 id) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].status == MarketStatus.Active, "not active");
        markets[id].status = MarketStatus.Paused;
    }

    function unpauseMarket(uint256 id) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].status == MarketStatus.Paused, "not paused");
        markets[id].status = MarketStatus.Active;
    }

    /**
     * @notice Manually resolve a market (admin only)
     * @param id Market ID
     * @param yesWins True if YES wins, false if NO wins
     */
    function resolveMarket(uint256 id, bool yesWins) external onlyRole(DEFAULT_ADMIN_ROLE) {
        Market storage m = markets[id];
        require(m.exists, "market not found");
        require(m.status == MarketStatus.Active || m.status == MarketStatus.Paused, "already resolved");
        require(!m.resolution.isResolved, "already resolved");

        m.status = MarketStatus.Resolved;
        m.resolution.yesWins = yesWins;
        m.resolution.isResolved = true;

        emit MarketResolved(id, yesWins, 0);
    }

    /**
     * @notice Auto-resolve market using Chainlink oracle (can be called by Chainlink Automation or resolver)
     * @param id Market ID
     */
    function autoResolveMarket(uint256 id) external {
        Market storage m = markets[id];
        require(m.exists, "market not found");
        require(m.status == MarketStatus.Active || m.status == MarketStatus.Paused, "not active");
        require(!m.resolution.isResolved, "already resolved");
        require(block.timestamp >= m.resolution.expiryTimestamp, "not expired");
        require(m.resolution.oracleType != OracleType.None, "manual resolution only");

        // Allow Chainlink resolver or admin to call this
        require(
            msg.sender == chainlinkResolver || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "not authorized"
        );

        uint256 currentPrice = _fetchOraclePrice(m.resolution);
        bool yesWins = _comparePrice(currentPrice, m.resolution.targetValue, m.resolution.comparison);

        m.status = MarketStatus.Resolved;
        m.resolution.yesWins = yesWins;
        m.resolution.isResolved = true;

        emit MarketResolved(id, yesWins, currentPrice);
    }

    /**
     * @notice Resolve market with explicit price (for Chainlink resolver)
     * @param id Market ID
     * @param price Price from oracle
     */
    function resolveMarketWithPrice(uint256 id, uint256 price) external {
        require(msg.sender == chainlinkResolver || hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not authorized");

        Market storage m = markets[id];
        require(m.exists, "market not found");
        require(m.status == MarketStatus.Active || m.status == MarketStatus.Paused, "not active");
        require(!m.resolution.isResolved, "already resolved");
        require(block.timestamp >= m.resolution.expiryTimestamp, "not expired");

        bool yesWins = _comparePrice(price, m.resolution.targetValue, m.resolution.comparison);

        m.status = MarketStatus.Resolved;
        m.resolution.yesWins = yesWins;
        m.resolution.isResolved = true;

        emit MarketResolved(id, yesWins, price);
    }

    /**
     * @notice Internal function to fetch price from Chainlink oracle
     */
    function _fetchOraclePrice(ResolutionConfig memory config) internal view returns (uint256) {
        if (config.oracleType == OracleType.ChainlinkFeed) {
            require(config.oracleAddress != address(0), "zero oracle address");
            AggregatorV3Interface priceFeed = AggregatorV3Interface(config.oracleAddress);

            (
                uint80 roundID,
                int256 price,
                uint256 startedAt,
                uint256 timeStamp,
                uint80 answeredInRound
            ) = priceFeed.latestRoundData();

            require(price > 0, "invalid price");
            require(timeStamp > 0, "round not complete");
            require(answeredInRound >= roundID, "stale data");
            require(block.timestamp - timeStamp < 3600, "data too old"); // 1 hour staleness check

            return uint256(price);
        } else if (config.oracleType == OracleType.ChainlinkFunctions) {
            // Chainlink Functions - this would require a separate contract
            // that handles Functions requests and stores results
            // For now, we'll need to store the result in a mapping when Functions callback executes
            revert("Chainlink Functions integration needed - use resolver contract");
        }
        revert("Invalid oracle type");
    }

    /**
     * @notice Internal function to compare price with target
     */
    function _comparePrice(uint256 price, uint256 target, Comparison comparison) internal pure returns (bool) {
        if (comparison == Comparison.Above) {
            return price > target;
        } else if (comparison == Comparison.Below) {
            return price < target;
        } else {
            return price == target;
        }
    }

    /**
     * @notice Check if market can be auto-resolved (for Chainlink Automation)
     */
    function checkUpkeep(uint256 id) external view returns (bool upkeepNeeded, bytes memory performData) {
        Market storage m = markets[id];
        upkeepNeeded = m.exists &&
            (m.status == MarketStatus.Active || m.status == MarketStatus.Paused) &&
            !m.resolution.isResolved &&
            block.timestamp >= m.resolution.expiryTimestamp &&
            m.resolution.oracleType != OracleType.None;

        performData = abi.encode(id);
    }

    /**
     * @notice Get market resolution config (for external contracts)
     */
    function getMarketResolution(uint256 id) external view returns (ResolutionConfig memory) {
        require(markets[id].exists, "market not found");
        return markets[id].resolution;
    }

    /**
     * @notice Grant MINTER_ROLE on a market's PositionTokens to an address
     * @dev Allows admins to grant minting permissions for emergency or administrative purposes
     * @param id Market ID
     * @param to Address to grant MINTER_ROLE to
     * @param isYes If true, grant on YES token; if false, grant on NO token
     */
    function grantMinterRole(uint256 id, address to, bool isYes) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].exists, "market not found");
        require(to != address(0), "zero address");

        PositionToken token = isYes ? markets[id].yes : markets[id].no;
        token.grantRole(token.MINTER_ROLE(), to);
    }

    /**
     * @notice Grant MINTER_ROLE on both PositionTokens to an address
     * @dev Convenience function to grant minting permissions on both YES and NO tokens
     * @param id Market ID
     * @param to Address to grant MINTER_ROLE to
     */
    function grantMinterRoleBoth(uint256 id, address to) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(markets[id].exists, "market not found");
        require(to != address(0), "zero address");

        markets[id].yes.grantRole(markets[id].yes.MINTER_ROLE(), to);
        markets[id].no.grantRole(markets[id].no.MINTER_ROLE(), to);
    }
}

// src/ChainlinkResolver.sol

/**
 * @title ChainlinkResolver
 * @notice Automatically resolves markets using Chainlink Price Feeds and Chainlink Automation
 * @dev This contract implements Chainlink Automation's KeeperCompatibleInterface
 */
contract ChainlinkResolver is AutomationCompatibleInterface {
    SpeculateCore public immutable core;

    // Mapping from market ID to price feed address
    mapping(uint256 => address) public marketPriceFeeds;

    event MarketRegistered(uint256 indexed marketId, address priceFeed);
    event MarketResolved(uint256 indexed marketId, bool yesWins, uint256 price);

    constructor(address _core) {
        require(_core != address(0), "zero core");
        core = SpeculateCore(_core);
    }

    /**
     * @notice Register a market with a Chainlink price feed
     * @param marketId Market ID
     * @param priceFeedAddress Chainlink AggregatorV3Interface address
     */
    function registerMarket(uint256 marketId, address priceFeedAddress) external {
        require(priceFeedAddress != address(0), "zero feed");
        require(marketPriceFeeds[marketId] == address(0), "already registered");

        // Verify it's a valid Chainlink feed
        AggregatorV3Interface feed = AggregatorV3Interface(priceFeedAddress);
        try feed.latestRoundData() returns (uint80, int256, uint256, uint256, uint80) {
            // Feed is valid
        } catch {
            revert("invalid feed");
        }

        marketPriceFeeds[marketId] = priceFeedAddress;
        emit MarketRegistered(marketId, priceFeedAddress);
    }

    /**
     * @notice Check if upkeep is needed (Chainlink Automation)
     * @param checkData Encoded start index (optional, defaults to 1). If empty, starts from market 1.
     * @return upkeepNeeded Whether upkeep is needed
     * @return performData Data to pass to performUpkeep (encoded market ID)
     * @dev Loops through all markets starting from the index in checkData (or 1 if empty)
     *      and finds the first market that needs resolution
     */
    function checkUpkeep(bytes calldata checkData) 
        external 
        view 
        override 
        returns (bool upkeepNeeded, bytes memory performData) 
    {
        // Get start index from checkData, or default to 1
        uint256 startIndex = 1;
        if (checkData.length > 0) {
            startIndex = abi.decode(checkData, (uint256));
        }

        // Get total market count
        uint256 totalMarkets = core.marketCount();

        // Limit to checking 50 markets per call to avoid gas issues
        // If we have more than 50 markets, we'll check them in batches
        uint256 maxMarketsToCheck = 50;
        uint256 endIndex = startIndex + maxMarketsToCheck;
        if (endIndex > totalMarkets) {
            endIndex = totalMarkets + 1; // +1 because we use < in the loop
        }

        // Loop through markets starting from startIndex
        for (uint256 i = startIndex; i < endIndex; i++) {
            // Check if this market needs resolution
            (bool needsUpkeep, bytes memory marketPerformData) = core.checkUpkeep(i);

            if (needsUpkeep) {
                // Found a market that needs resolution
                return (true, marketPerformData);
            }
        }

        // No markets in this batch need resolution
        // If there are more markets to check (endIndex <= totalMarkets), 
        // they will be checked on the next upkeep cycle when it restarts from market 1
        // This is safe because markets will be checked regularly
        return (false, "");
    }

    /**
     * @notice Perform upkeep (Chainlink Automation)
     * @param performData Encoded market ID
     */
    function performUpkeep(bytes calldata performData) external override {
        uint256 marketId = abi.decode(performData, (uint256));

        // Check if market can be auto-resolved
        (bool upkeepNeeded, ) = core.checkUpkeep(marketId);
        require(upkeepNeeded, "upkeep not needed");

        // Get market resolution config
        SpeculateCore.ResolutionConfig memory resolution = core.getMarketResolution(marketId);

        // Get price feed address (prefer registered feed, fallback to market's oracle address)
        address priceFeedAddress = marketPriceFeeds[marketId];
        if (priceFeedAddress == address(0)) {
            priceFeedAddress = resolution.oracleAddress;
        }
        require(priceFeedAddress != address(0), "feed not registered");

        uint256 currentPrice = _getChainlinkPrice(priceFeedAddress);

        // Resolve market with price
        core.resolveMarketWithPrice(marketId, currentPrice);

        // Get the resolution result to determine winner
        SpeculateCore.ResolutionConfig memory resolvedConfig = core.getMarketResolution(marketId);
        bool yesWins = resolvedConfig.yesWins;

        emit MarketResolved(marketId, yesWins, currentPrice);
    }

    /**
     * @notice Get latest price from Chainlink feed
     * @param priceFeedAddress Chainlink AggregatorV3Interface address
     * @return price Latest price (scaled to 8 decimals for most feeds, adjust as needed)
     */
    function _getChainlinkPrice(address priceFeedAddress) internal view returns (uint256) {
        AggregatorV3Interface priceFeed = AggregatorV3Interface(priceFeedAddress);

        (
            uint80 roundID,
            int256 price,
            uint256 startedAt,
            uint256 timeStamp,
            uint80 answeredInRound
        ) = priceFeed.latestRoundData();

        require(price > 0, "invalid price");
        require(timeStamp > 0, "round not complete");
        require(answeredInRound >= roundID, "stale data");

        // Convert int256 to uint256 (price feeds return int256)
        require(price > 0, "negative price");
        return uint256(price);
    }

    /**
     * @notice Compare price with target value
     */
    function _comparePrice(
        uint256 price, 
        uint256 target, 
        SpeculateCore.Comparison comparison
    ) internal pure returns (bool) {
        if (comparison == SpeculateCore.Comparison.Above) {
            return price > target;
        } else if (comparison == SpeculateCore.Comparison.Below) {
            return price < target;
        } else {
            return price == target;
        }
    }

}
